$_MODULE['<{adpmicrodatos}prestashop>help_9ffdb95250e26c7a6b468126ac7c75b0'] = 'So funktioniert es';
$_MODULE['<{adpmicrodatos}prestashop>help_7b4d3b59178df2c6c654ee527ce24e69'] = 'Welche Mikrodaten werden durch dieses Modul eliminiert?';
