$_MODULE['<{lgcanonicalurls}prestashop>info_message_82a6a36aec7f2f7cf893f3f3e0fc995a'] = 'Notre module s\'appuie sur cette fonctionnalité pour fonctionner sur Prestashop 1.7.x par défaut, en effectuant des changements sur la balise canonique en interne =';
