$_MODULE['<{lgcanonicalurls}prestashop>info_message_82a6a36aec7f2f7cf893f3f3e0fc995a'] = 'Il nostro modulo si basa su questa funzionalità per funzionare su Prestashop 1.7.x per impostazione predefinita, apportando modifiche al canonico internamente.';
$_MODULE['<{lgcanonicalurls}prestashop>info_message_50fa9ef2c74859474e93a18776b41bf2'] = 'Usando questa opzione, il nostro modulo utilizzerà l\'hook standard displayHeader invece della funzionalità Prestashop Core per mostrare il tag.';
$_MODULE['<{lgcanonicalurls}prestashop>info_message_6cae151e25c8b314ca3c3e9562f6915a'] = 'Il tuo tema deve essere compatibile con questa funzionalità per ottenere il tag.';
$_MODULE['<{lgcanonicalurls}prestashop>info_message_19c5d1faf93fc0c2af4d3e06a5da31b2'] = 'Se non vedi il tag, di solito è perché il tema non supporta questa nuova funzionalità.';
