$_MODULE['<{lgcookieslaw}prestashop>view_help_ad32ba59a021a0d085e49b3659b49a61'] = 'Χρώμα γραμματοσειράς';
$_MODULE['<{lgcookieslaw}prestashop>view_help_d38bd23d681913198f82db37edfac10a'] = 'Επιλέξτε το χρώμα του κειμένου του banner cookie.';
$_MODULE['<{lgcookieslaw}prestashop>view_help_1e4e2a7eebabcf48d391dce93fb85c94'] = 'Μήνυμα πανό';
$_MODULE['<{lgcookieslaw}prestashop>view_help_0c0b6dd728522d22659c2278b4e83dfd'] = 'Γράψτε και προσαρμόστε το στυλ του κειμένου cookie. Χρησιμοποιήστε τη γραμμή έκδοσης για να προσαρμόσετε το κείμενο (έντονη, πλάγια, υπογραμμισμένη, χρώμα, γραμματοσειρά, μέγεθος, σύνδεσμος, κέντρο...).';
$_MODULE['<{lgcookieslaw}prestashop>view_help_9b051d3f00445a8741c61634003ce061'] = 'Εάν έχετε πολύγλωσσο κατάστημα, κάντε κλικ στο κουμπί δίπλα στο πεδίο κειμένου για να αλλάξετε γλώσσα (αυτόματη αναγνώριση των γλωσσών του καταστήματος) και να προσθέσετε διαφορετικό κείμενο ανά γλώσσα.';
$_MODULE['<{lgcookieslaw}prestashop>view_help_0ddc62632da841ec3f394440a09e4e95'] = 'Τίτλος κουμπιού \"Αποδοχή\"';
$_MODULE['<{lgcookieslaw}prestashop>view_help_779f35d986588e5f5842be9dcd73f8de'] = 'Τίτλος κουμπιού αποδοχής.';
$_MODULE['<{lgcookieslaw}prestashop>view_help_10830d5011ef0c70f14563f6538123ec'] = 'Αν έχετε πολύγλωσσο κατάστημα, κάντε κλικ στο κουμπί στα δεξιά του πεδίου για να αλλάξετε γλώσσα (αυτόματη αναγνώριση των γλωσσών του καταστήματός σας) και προσαρμόστε το όνομα του κουμπιού σε όλες τις γλώσσες του καταστήματός σας.';
$_MODULE['<{lgcookieslaw}prestashop>view_help_81bbf517d78dc4f1f1518632d8b97a71'] = 'Χρώμα φόντου κουμπιού \"Αποδοχή\"';
$_MODULE['<{lgcookieslaw}prestashop>view_help_9832d8a7ba11e7a0158b71858a775afb'] = 'Επιλέξτε το χρώμα φόντου του κουμπιού \"Αποδοχή\".';
$_MODULE['<{lgcookieslaw}prestashop>view_help_c5630a0f2a080011910b7c3bdbd6777f'] = 'Χρώμα γραμματοσειράς κουμπιού \"Αποδοχή\"';
$_MODULE['<{lgcookieslaw}prestashop>view_help_0cf750edfe91005d5ff33372b4010d09'] = 'Επιλέξτε το χρώμα του κειμένου του κουμπιού \"Αποδοχή\".';
$_MODULE['<{lgcookieslaw}prestashop>view_help_dc1e867856ad0646bd6e2be074c78d4b'] = 'Τίτλος συνδέσμου \"Πληροφορίες\"';
$_MODULE['<{lgcookieslaw}prestashop>view_help_430203aaa7abd336cb73d4584356487d'] = 'Επιλέξτε τον τίτλο του συνδέσμου \"Πληροφορίες\".';
$_MODULE['<{lgcookieslaw}prestashop>view_help_f3b66a89f7907a3c502bd166b32c2dc8'] = 'CMS URL του συνδέσμου \"Πληροφορίες\"';
$_MODULE['<{lgcookieslaw}prestashop>view_help_b4597cbc10fc8029aad162c38802240f'] = 'Επιλέξτε τη σελίδα του cms στην οποία θέλετε να μεταβούν οι χρήστες όταν κάνουν κλικ στο σύνδεσμο \"Πληροφορίες\".';
$_MODULE['<{lgcookieslaw}prestashop>view_help_28aee21bbd52bac1434f8c69dcd1f94e'] = 'Για να προσαρμόσετε το περιεχόμενο αυτής της σελίδας, μεταβείτε στο μενού \"Προτιμήσεις → CMS\".';
$_MODULE['<{lgcookieslaw}prestashop>view_help_42e2a578797de3deb01c77d45b72072d'] = 'Μπορείτε να επεξεργαστείτε μια υπάρχουσα σελίδα ή να δημιουργήσετε μια νέα σελίδα αφιερωμένη στη χρήση των cookies.';
$_MODULE['<{lgcookieslaw}prestashop>view_help_4181319c42e95d4a4173e6d726cc6063'] = 'Στόχος συνδέσμου \"Πληροφορίες\"';
$_MODULE['<{lgcookieslaw}prestashop>view_help_86901b48a76b14eb36781ca99fc461bd'] = 'Επιλέξτε τον τρόπο με τον οποίο θα ανοίξει ο σύνδεσμος \"Πληροφορίες\" (σε νέο ή στο ίδιο παράθυρο).';
$_MODULE['<{lgcookieslaw}prestashop>view_help_d8a38f5d88068395c76074c9606340ca'] = 'Εμφάνιση του κουμπιού \"Απόρριψη\"';
$_MODULE['<{lgcookieslaw}prestashop>view_help_0fb42d9f3b8cc2bc5a9e78e60ed5f3ed'] = 'Εάν ενεργοποιήσετε αυτή την επιλογή, το κουμπί \"Απόρριψη\" θα εμφανίζεται στο πανό.';
$_MODULE['<{lgcookieslaw}prestashop>view_help_42b9d7b7f0cfe3c759244c58b692dfb9'] = 'Τίτλος κουμπιού \"Απόρριψη\"';
$_MODULE['<{lgcookieslaw}prestashop>view_help_356d4278f8592e9611ad30ae065d86ed'] = 'Τίτλος κουμπιού απόρριψης.';
$_MODULE['<{lgcookieslaw}prestashop>view_help_13c6fc560b845015c4971e8de36e08e2'] = 'Χρώμα φόντου κουμπιού \"Απόρριψη\"';
$_MODULE['<{lgcookieslaw}prestashop>view_help_59c68b3860ddd64dd36e414162b1e536'] = 'Επιλέξτε το χρώμα φόντου του κουμπιού \"Απόρριψη\".';
$_MODULE['<{lgcookieslaw}prestashop>view_help_08b9f1d10f52e4faac0f1c8386d3da38'] = 'Χρώμα γραμματοσειράς κουμπιού \"Απόρριψη\"';
$_MODULE['<{lgcookieslaw}prestashop>view_help_dcf7470d827f8e6afb2ce87db4e112a2'] = 'Επιλέξτε το χρώμα του κειμένου του κουμπιού \"Απόρριψη\".';
$_MODULE['<{lgcookieslaw}prestashop>view_help_c8e6ab2abeb01f3355f46753bd41408f'] = 'Σε αυτή την ενότητα ρυθμίζετε το σκοπό που αντιστοιχεί καλύτερα στα cookies σας. Από προεπιλογή θα βρείτε 5 τύπους:';
$_MODULE['<{lgcookieslaw}prestashop>view_help_80323fc65493c18f27173d21f97e941f'] = 'Λειτουργικά cookies';
$_MODULE['<{lgcookieslaw}prestashop>view_help_01e2ba02b2a15fb82e58debfcc6b91c8'] = 'Τα λειτουργικά cookies είναι απολύτως απαραίτητα για την παροχή των υπηρεσιών του καταστήματος, καθώς και για τη σωστή λειτουργία του, για το λόγο αυτό δεν πρέπει να διαγράψετε αυτή τη λειτουργία και πρέπει να είναι πάντα ενεργοποιημένη και το πεδίο \"Τεχνικό\" πρέπει να είναι ενεργοποιημένο.';
$_MODULE['<{lgcookieslaw}prestashop>view_help_3d92fd5ab89ddc19ab43a885abcccbf8'] = 'Cookies διαφήμισης';
$_MODULE['<{lgcookieslaw}prestashop>view_help_19481026d4eb66edd4a96fb2e597526d'] = 'Τα διαφημιστικά cookies συλλέγουν πληροφορίες σχετικά με τις διαφημίσεις που προβάλλονται στους χρήστες του ιστότοπου.';
$_MODULE['<{lgcookieslaw}prestashop>view_help_8c1778a47c661bee5ce4ea4ca5e313ba'] = 'Cookies επιδόσεων';
$_MODULE['<{lgcookieslaw}prestashop>view_help_ecf60c4d24e45e939e048b8a2d329c78'] = 'Τα αναλυτικά cookies συλλέγουν πληροφορίες σχετικά με την εμπειρία περιήγησης του χρήστη στο κατάστημα. Θα είναι ο προεπιλεγμένος σκοπός που θα ενεργοποιήσει τη \"Λειτουργία συναίνεσης\" της Google. Για περισσότερες πληροφορίες, διαβάστε την ενότητα:';
$_MODULE['<{lgcookieslaw}prestashop>view_help_c099e3fa29572b1e3b94dfa0b9cff108'] = 'Τα cookies επιδόσεων χρησιμοποιούνται για τη βελτίωση της εμπειρίας περιήγησης και τη βελτιστοποίηση της λειτουργίας του καταστήματος.';
$_MODULE['<{lgcookieslaw}prestashop>view_help_a97befd9375dbde1c8dc419c1b62c045'] = 'Άλλα cookies';
$_MODULE['<{lgcookieslaw}prestashop>view_help_a3224e27136329979e56957c6cc18844'] = 'Πρόκειται για cookies χωρίς σαφή σκοπό ή για cookies που βρίσκονται ακόμη στη διαδικασία ταξινόμησης.';
$_MODULE['<{lgcookieslaw}prestashop>view_help_662faee4f298472fd3d1404cc9c8db28'] = 'ΘΥΜΗΘΕΙΤΕ: Μπορείτε να τροποποιήσετε τους προεπιλεγμένους τύπους cookies, να προσθέσετε νέους ή να διαγράψετε τους υπάρχοντες. Σας υπενθυμίζουμε ότι δεν πρέπει να διαγράφετε λειτουργικά cookies.';
$_MODULE['<{lgcookieslaw}prestashop>view_help_908375ef5b64d09b1d0045ea13f6dc44'] = 'Σκοποί διαμόρφωσης';
$_MODULE['<{lgcookieslaw}prestashop>view_help_4d3d769b812b6faa6b76e1a8abaece2d'] = 'Ενεργό';
$_MODULE['<{lgcookieslaw}prestashop>view_help_bb66bdd67619cf29e1554f0c6dd78fed'] = 'Αυτή η επιλογή επιτρέπει την εμφάνιση ή μη του σκοπού στο πανό.';
