$_MODULE['<{lgcookieslaw}prestashop>view_help_fac9e8c9bf4f9114172961763838d02f'] = 'Dar un nombre relevante a la cookie utilizada por nuestro módulo para guardar el contenido del usuario (nombre por defecto: lgcookieslaw).';
